import React, { Component } from 'react'
import Result from './Result';
import Navbar from './Navbar';
import Jumbotron from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


export default class App extends Component {
    constructor(){
    super();
      this.state={
        items:'',
        showItems:false
      }
      this.handleChange=this.handleChange.bind(this);
      this.handleSubmit=this.handleSubmit.bind(this);
    }

     handleChange(e){
       this.setState({
         items:e.target.value
       })
     }

     handleSubmit(e){
      console.log("value:"+this.state.items)
      e.preventDefault();
      this.setState({
        showItems:true
      })
     }
  render() {
    return (
     <div>
          <Navbar/>
          <Jumbotron>
          <p>
            Enter the things you would like to buy.
          </p>
        </Jumbotron>

        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Control value={this.state.items} onChange={this.handleChange}type="email" placeholder="Enter email" />
          </Form.Group>
          <Button onClick={this.handleSubmit}variant="primary" type="submit">
            Submit
          </Button>
          {this.state.items}
        </Form>
        <Result items={this.state.items} showItems={this.state.showItems} />       
      </div>
    )
  }
}
